import {useState} from 'react';

const Header = () => {
    const orange = 'rgba(246, 245, 252, 0.247), rgba(117, 19, 93, 0.055)';
    const purple = 'rgba(246, 245, 252, 0.247), rgba(117, 19, 93, 0.73)';

    const [hover, setHover] = useState(false);
    const [color, setColor] = useState(orange);

    const handleHover = () => {
        setHover(!hover);
    }

    const style = {
        backgroundImage: `linear-gradient(to bottom, ${color}, url("../img/sbimage.jpeg")`
    }

    return (
        <header className="header header-background jumbotron-fluid" style={style}>
            <div className="container-fluid py-5" onMouseEnter={handleHover} onMouseLeave={handleHover}>
            <div className="row text-white text-center">
                <div className="col">
                    {!hover && <p id="header-text-1" className="header-text">My name is Jon</p>}
                    { hover && <p id="header-text-2" className="header-text">And I'm a web-developer from Santa Barbara.</p>}
                </div>
            </div>
            </div>
      </header>
    )
}

export default Header
