const Skills = () => {

const listItems = document.querySelectorAll('.skills .list-group-item');

function invertColors(element) {
    const listItem = element.target;
    const listText = listItem.innerText.toLowerCase();
    const normalClass = "list-item-" + listText;
    const invertedClass = normalClass + "-inverted";
    const currentClassList = listItem.className;
    listItem.style.transition = "all 0.3s ease-in-out";

    if (currentClassList.indexOf(invertedClass) >= 0) {
        listItem.classList.remove(invertedClass);
        listItem.classList.add(normalClass);
    } else {
        listItem.classList.remove(normalClass);
        listItem.classList.add(invertedClass);
    }
}

listItems.forEach(listItem => {
    listItem.addEventListener('mouseover', (event) => {
        invertColors(event);
    });
});

listItems.forEach(listItem => {
    listItem.addEventListener('mouseout', (event) => {
        invertColors(event);
    });
});

    return (
        <div>
            <section className="skills p-3 p-lg-4" id="skills">
                <div className="container-fluid">
                <div className="row text-center">
                    <div className="col">
                    <h5 className="display-4 text-white mb-4">Skills</h5>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                    <ul className="list-group text-center">
                        <li className="list-group-item list-item-html5 font-weight-bold mb-2 w-75 mx-auto">HTML5</li>
                        <li className="list-group-item list-item-css3 font-weight-bold mb-2 w-75 mx-auto">CSS3</li>
                        <li className="list-group-item list-item-javascript font-weight-bold mb-2 w-75 mx-auto">JavaScript</li>
                        <li className="list-group-item list-item-sass font-weight-bold mb-2 w-75 mx-auto">Sass</li>
                        <li className="list-group-item list-item-github font-weight-bold mb-2 w-75 mx-auto">Github</li>
                        <li className="list-group-item list-item-git font-weight-bold mb-2 w-75 mx-auto">Git</li>
                        <li className="list-group-item list-item-bootstrap font-weight-bold mb-2 w-75 mx-auto">Bootstrap</li>
                        <li className="list-group-item list-item-sql font-weight-bold mb-2 w-75 mx-auto">SQL</li>
                    </ul>
                    </div>
                </div>
                </div>

            </section>
        <hr/>
        </div>
    )
}

export default Skills
