import {useState} from 'react';

const Header = () => {
    const [hover, setHover] = useState(false);
    const [backgroundClass, setBackgroundClass] = useState('background-image-orange');

    const handleHover = () => {
        setHover(!hover);
    }


    return (
        <header className="{`header header-background jumbotron-fluid ${backgroundImageClass}`}">
            <div className="container-fluid py-5" onMouseEnter={handleHover} onMouseLeave={handleHover}>
            <div className="row text-white text-center">
                <div className="col">
                    {!hover && <p id="header-text-1" className="header-text">My name is Jon</p>}
                    { hover && <p id="header-text-2" className="header-text">And I'm a web-developer from Santa Barbara.</p>}
                </div>
            </div>
            </div>
      </header>
    )
}

export default Header
