import {useState} from 'react';

const Header = () => {
    const [hover, setHover] = useState(false);

    const handleMouseEnter = () => {
        setHover(true);
    }


    return (
        <header className="header header-background background-image-purple jumbotron-fluid">
            <div className="container-fluid py-5" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
            <div className="row text-white text-center">
                <div className="col">
                    <p id="header-text-1" className="header-text">My name is Jon</p>
                    <p id="header-text-2" className="header-text hidden">And I'm a web-developer from Santa Barbara.</p>
                </div>
            </div>
            </div>
      </header>
    )
}

export default Header
