import {useState, useEffect} from 'react';

//Import Components
import Navbar from '../Navbar/Navbar';
import Header from '../Header/Header';
import About from '../About/About';
import Projects from '../Projects/Projects';
import Skills from '../Skills/Skills';
import Contact from '../Contact/Contact';
import Footer from '../Footer/Footer';

import {projects} from '../json/projects.json';

const App = () => {
  const [skill, setSkill] = useState('All');
  const [data, setData] = useState(projects);

  //Function that filters data state based on skill
  const filterBySkill = (skill) => {
    if (skill !== 'All' && skill !== '' && skill !== 'none') {
      const newData = data.filter(d => d.skills.includes(skill));
      setData(newData);
    }
  }

  //Function that handle change to sill that is selected
  const handleSkillChange = (skill) => {
    setSkill(skill);
    filterBySkill(skill);
  }

  return (
    <div className="App">
      <Navbar/>
      <Header/>
      <About/>
      <Projects projects={data} handleSkillChange={handleSkillChange} skill={skill}/>
      <Skills/>
      <Contact/>
      <Footer/>
    </div>
  );
}

export default App;
