import {useState, useEffect} from 'react';
import axios from 'axios';

//Import config 
import config from '../config';

const Project = ({projects}) => {

     //Set state
  const [skill, setSkill] = useState('All');
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    //Set isLoading to true
    setIsLoading(true);
    //Get data from projects api
    axios.get(`${config.apiBaseUrl}/projects`)
    .then(res => {
        setData(res.data.data);
        setIsLoading(false);
    })
  }, []);

    //Filter data by skill and update data state
    const filterBySkill = (skill) => {
        //If skill is All, use projects api.  If skill is not All, use projects/:skill api
        const apiUrl = skill ==='All' ? `${config.apiBaseUrl}/projects` : `${config.apiBaseUrl}/projects/${skill}`;

        //Get project by skill from skills api
        axios.get(`${apiUrl}`)
        .then(res => {
            setData(res.data.data);
        })
      }

      //Function that handle change to skill that is selected
      const handleSkillChange = (skill) => {
        setSkill(skill);
        filterBySkill(skill);
      }

    const projectElements = 
    projects
    ?
            projects.map((project, index) =>

        (
            <div className="text-center mb-4 col-xs-12 col-md-6 col-lg-4" key={index} data-index={index}>
                <div className="card text-center bg-light border-dark rounded h-100 p-4">
                    <img src={`${process.env.PUBLIC_URL}/img/${project.image}`} className="card-img-top img-fluid mb-4" alt={project.name}/>
                    <div className="card-body d-flex flex-column text-center">
                        <h5 className="card-title">{project.name}</h5>
                        <p className="card-text">{project.description}</p>
                        <p className="skills-used mt-auto card-text"><span className="font-weight-bold">Skills Used:</span> {project.skills}</p>
                        <a href={`https://github.com/jonhayjr/Treehouse-FEWD-Project${project.number}`} className="btn btn-secondary mb-4" target="_blank" rel="noopener noreferrer">View Code</a>
                        <a href={`https://jonhayjr.com/Treehouse-FEWD-Project${project.number}/`} className="btn btn-dark" target="_blank" rel="noopener noreferrer">Go to Website</a>
                    </div> 
                </div>
            </div>
        )
    )
    : '';

    return (
        <div className="container-fluid">
            <div className="row">
                {
                projectElements
                }
                </div>
        </div>
    )
}

export default Project
